import React, { useReducer } from 'react';
import './App.css';

function reducer (state, action) {
  switch (action.type) {
    case "ADD":
      return {
        currentId: state.currentId + 1,
        notes: [...state.notes, {id: state.currentId, note: <Note notes={action.notes} reducer={action.reducer}/>}]
      };
      case "REMOVE":
        return {
          ...state,
          notes: state.notes.filter((note) => note.id !== action.id)
        }

      default:
        return (state);
  }
}

const Note = (props) => {
  const { notes } = props.notes;
  const dispatch = props.reducer;

  const removeNote = () => {
    dispatch({type: "REMOVE"});
  }

  return (
    <div>
      <div className="header_note">
        <i className="fas fa-trash-alt" onClick={removeNote}></i>
        <i className="fas fa-edit"></i>
      </div>
      <div className="writting">
        <textarea className="zoneToWrite"></textarea>
      </div>
    </div>
  )
}

function NotesApp() {
  const [{ notes }, dispatch] = useReducer(reducer, {
    currentId: 0,
    notes: []
  });

  const addNote = () => {
    // dispatch({type: "ADD", reducer: [{ notes }, dispatch]});
    dispatch({type: "ADD", notes: notes, reducer: dispatch});
  }

  return (
    <div className="container">
      <button className="button" onClick={addNote}>
            <i className="fas fa-plus"></i>
            Add Note
      </button>
      <ul className="notes">
        {notes.map(({id, note, disabled}) => 
          <li key={id} className="note">
            {note}
          </li>)}
      </ul>
    </div>
  );
}

export default NotesApp;

























