import React from 'react'
import './App.css';

function reducer (state, action) {
  switch (action.type) {
    case "ADD":
      return {
        state
      };
      default:
        return (state);
  }
}

function Note () {
  return (
    <div className="note">
      <div className="header_note">
        <i className="fas fa-trash-alt"></i>
        <i className="fas fa-edit"></i>
      </div>
      <div className="writting">
        <textarea className="zoneToWrite"></textarea>   
      </div>
    </div>
  )
}

function notesApp() {
  const [{ notes }, dispatch] = React.useReducer(reducer, {
    currentId: 0,
    note: []
  });

  const addNote = () => {
    dispatch({type: "ADD"});
  }

  return (
    <div className="container">
      <button className="button" onClick={addNote}>
            <i className="fas fa-plus"></i>
            Add Note
      </button>
      <ul className="notes">
        {notes.map(({id, note, disabled}) => 
        <li key={id}>
          {note}
        </li>)}
      </ul>
    </div>
  );
}

export default notesApp;



